<script lang="ts">
import { defineComponent, ref, watch } from 'vue';
import { useToast } from "vue-toastification";
import { useMutation } from '@vue/apollo-composable';
import { useQuery } from '@vue/apollo-composable';
import PasswordField from '../../components/ui/forms/PasswordField.vue';
import { useAuthStore } from '../../stores/authStore';
import { LOGIN_MUTATION } from '../../controllers/graphql/mutations/auth/loginMutations';
import { GET_USUARIOS } from "../../controllers/graphql/queries/userQueries";
import router from '../../router/router';

export default defineComponent({
    components: {
        PasswordField
    },
    setup() {
        const email = ref('');
        const password = ref('');
        const loading = ref(false);
        const toast = useToast();
        const authStore = useAuthStore();
        const userEmail = ref('');

        const { mutate: login, onDone, onError } = useMutation(LOGIN_MUTATION);

        onDone((data) => {
            loading.value = false;
            if (data.data.login.loginResponse.token) {
                // Guardar el token en el estado global
                authStore.login(data.data.login.loginResponse.token);
                userEmail.value = email.value; // Trigger watcher
            } else {
                toast.error('No se ha podido iniciar sesión, por favor intenta de nuevo.');
            }
        });

        onError((error) => {
            loading.value = false;
            console.log('Error:', error);
        });

        const loginUser = () => {
            loading.value = true;
            login({
                input: {
                    email: email.value,
                    password: password.value
                }
            });
        };

        // Buscar usuarios para obtener el rol del usuario
        const { result } = useQuery(GET_USUARIOS);
        watch(userEmail, (newEmail) => {
            if (newEmail) {
                watch(result, (response) => {
                    const user = response?.usuarios.items.find((user: any) => user.correo === newEmail);
                    if (user) {
                        // Guardar el rol en el estado global
                        let userRole = user.rol.rol1;
                        authStore.setRole(userRole);
                        
                        let userId = user.usuarioId;
                        authStore.setUserId(userId);

                        toast.success('Login exitoso!');

                        if (userRole === 'Admin') {
                            router.push('/admin/dashboard');
                        } else if (userRole === 'Applicant') {
                            router.push('/user/dashboard');
                        }else if (userRole === 'Inspector') {
                            router.push('/inspectordashboard');
                        } else if (userRole === 'Evaluator') {
                            router.push('/lista-productos-evaluador');
                        } else {
                            toast.error('Hubo un problema al determinar el rol de usuario.');
                        }
                    }
                    else {
                        toast.error('Hubo un problema al determinar el rol de usuario.');
                    }
                }, { immediate: true });
            }
        });

        return {
            email,
            password,
            loading,
            loginUser
        };
    }
});
</script>

